import { height } from "../utilities/density";
import { inputFieldStyles } from "../patterns/input-field";
import { multiply, toPx } from "@microsoft/fast-jss-utilities";
import { designUnit } from "../utilities/design-system";
const styles = {
    textArea: Object.assign({}, inputFieldStyles(), { height: height(2), "padding-top": toPx(multiply(designUnit, 1.5)), "padding-bottom": toPx(multiply(designUnit, 1.5)), "max-width": "100%" }),
};
export default styles;
