import { directionSwitch, format } from "@microsoft/fast-jss-utilities";
import { height, horizontalSpacing } from "../utilities/density";
import { inputFieldStyles } from "../patterns/input-field";
const styles = {
    numberField: Object.assign({}, inputFieldStyles(), { height: height(), padding: directionSwitch(format("0 6px 0 {0}", horizontalSpacing()), format("0 {0} 0 6px", horizontalSpacing())), "-moz-appearance": "textfield", "&::-webkit-inner-spin-button": {
            appearance: "none",
            "-webkit-appearance": "none",
        } }),
};
export default styles;
