import manageJss from "@microsoft/fast-jss-manager-react";
import { BadgeStyles } from "@microsoft/fast-components-styles-msft";
import MSFTBadge, { BadgeSize, } from "./badge";
import badgeSchema from "./badge.schema";
/*
 * The type returned by manageJss type is very complicated so we'll let the
 * compiler infer the type instead of re-declaring just for the package export
 */
/* tslint:disable-next-line:typedef */
const Badge = manageJss(BadgeStyles)(MSFTBadge);
export { Badge, badgeSchema, BadgeSize, };
