import React from 'react';
import { Grid, Column } from "@microsoft/fast-layouts-react";
import { Image, Pivot } from "@microsoft/fast-components-react-msft";

import { info } from "../data/info";

export default function MyMain(props) {
    return (
        <Grid id="main">
              <Column
                position={1}
                span={5}
              >
                <Image
                  className="imageDisplay"
                  id={`${info.sections[props.imageId].headingText}-image`}
                  src={info.sections[props.imageId].image.src}
                  alt={info.sections[props.imageId].image.alt}
                />
              </Column>

              <Column
                position={7}
                span={8}
              >

                <Pivot
                  id="myPivot"
                  label="Pivot Component containing information about Samantha Orcutt"
                  items={info.sections.map((x, i) => {
                    let obj = {};
                    obj.tab = (className) => (   
                      <p  className={className} id={`tab-${i}`}>{x.headingText} </p>
                    );
                    obj.content = (className) => (
                      <p className={className}>{x.paragraphText}</p>
                    );
                    obj.id = i.toString();
                    // uniqueId();
                    return obj;
                  })
                  }
                />
              </Column>
            </Grid>

    )

}
